import React,{useState,useRef} from 'react';
import './contact.scss';

import emailjs from '@emailjs/browser';


export default function Contact() {
   
  // const[message,setMessage]=useState(false);

  // const handleSubmit=(e)=>{
  //       e.preventDefault();
  //     setMessage(true);
  // }

    const form = useRef();
  
    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_3yr2a3i', 'template_2w9dw3f', form.current, 'MvBsJagrlXL-FVTMN')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
      }
    


  return (
    <div className="contact" id="contact">
      <div className="left">
        <img src="assets/shake.svg" alt=""/>
      </div>
      <div className="right">
           <h2>Contact.</h2>
           <form method="get" action="javascript: void(0);" id="login-form" class="login-form" autocomplete="off" role="main">
  <h1 class="a11y-hidden">Login Form</h1>
  <div>
    <label class="label-email">
      <input type="email" class="text" name="email" placeholder="Email" tabindex="1" required />
      <span class="required">Email</span>
    </label>
  </div>
  <input type="checkbox" name="show-password" class="show-password a11y-hidden" id="show-password" tabindex="3" />
  <label class="label-show-password" for="show-password">
    <span>Show Password</span>
  </label>
  <div>
    <label class="label-password">
      <input type="text" class="text" name="password" placeholder="Password" tabindex="2" required />
      <span class="required">Password</span>
    </label>
  </div>
  <input type="submit" value="Log In" />
  <div class="email">
    <a href="#">Forgot password?</a>
  </div>
  <figure aria-hidden="true">
    <div class="person-body"></div>
    <div class="neck skin"></div>
    <div class="head skin">
      <div class="eyes"></div>
      <div class="mouth"></div>
    </div>
    <div class="hair"></div>
    <div class="ears"></div>
    <div class="shirt-1"></div>
    <div class="shirt-2"></div>
  </figure>
</form>
      </div>
    </div>
  )
  
}
