import { useEffect, useState } from "react";
import PortofolioList from "../portofolioList/PortofolioList";
import "./portofolio.scss";
import {
  Garduri,
  Balustrade,
  Grilaje,
  Porti,
  
} from "../../data";

export default function Portofolio() {
  const [selected, setSelected] = useState("featured");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "garduri",
      title: "Garduri",
    },
    {
      id: "balustrade",
      title: "Balustrade",
    },
    {
      id: "grilaje",
      title: "Grilaje",
    },
    {
      id: "porti",
      title: "Porti",
    },
    
  ];

  useEffect(() => {
    switch (selected) {
      case "garduri":
        setData(Garduri);
        break;
      case "":
        setData(Balustrade);
        break;
      case "mobile":
        setData(mobilePortfolio);
        break;
      case "design":
        setData(designPortfolio);
        break;
      case "content":
        setData(contentPortfolio);
        break;
      default:
        setData(featuredPortfolio);
    }
  }, [selected]);

  return (
    <div className="portofolio" id="fotografii">
      <h1>Fotografii</h1>
      <ul>
        {list.map((item) => (
          <PortofolioList
            title={item.title}
            actived={selected === item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        {data.map((d) => (
          <div className="item">
            <img
              src={d.img}
              alt=""
            />
            <h3>{d.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}
