import React,{useState,useRef} from 'react';
import './contact.scss';

import emailjs from '@emailjs/browser';


export default function Contact() {
   
  // const[message,setMessage]=useState(false);

  // const handleSubmit=(e)=>{
  //       e.preventDefault();
  //     setMessage(true);
  // }

    const form = useRef();
  
    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_3yr2a3i', 'template_2w9dw3f', form.current, 'MvBsJagrlXL-FVTMN')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
      }
    


  return (
    <div className="contact" id="contact">
      <div className="left">
        <img src="assets/shake.svg" alt=""/>
      </div>
      <div className="right">
           <h2>Contact.</h2>

           <div className="input-body">
           <form method="get" id="login-form" className="login-form" autoComplete="off" role="main">
  <h1 className="a11y-hidden">Login Form</h1>
  <div>
    <label className="label-email">
      <input type="email" className="text" name="email" placeholder="Email"  required />
      <span className="required">Email</span>
    </label>
  </div>
  <input type="checkbox" name="show-password" className="show-password a11y-hidden" id="show-password" tabIndex="3" />
  <label className="label-show-password" htmlFor="show-password">
    <span>Show Password</span>
  </label>
  <div>
    <label className="label-password">
      <input type="text" className="text" name="password" placeholder="Password"  required />
      <span className="required">Password</span>
    </label>
  </div>
  <input type="submit" value="Log In" />
  <div className="email">
    <a href="#">Forgot password?</a>
  </div>
  <figure aria-hidden="true">
    <div className="person-body"></div>
    <div className="neck skin"></div>
    <div className="head skin">
      <div className="eyes"></div>
      <div className="mouth"></div>
    </div>
    <div className="hair"></div>
    <div className="ears"></div>
    <div className="shirt-1"></div>
    <div className="shirt-2"></div>
  </figure>
</form>
      </div>
    </div>
           </div>
    
  )
  
}
